import { Injectable } from '@angular/core';
import { Observable } from 'rxjs';
import { ApiService } from './api.service';
import { CriarOrcamentoResponse, OrcamentoDetalhe, OrcamentoListaItem, OrcamentoPayload } from '../models/orcamento.model';
import {environment} from "../../environments/environment";

@Injectable({ providedIn: 'root' })
export class OrcamentosService {
    private base = environment.apiUrl + 'Orcamentos';

    constructor(private api: ApiService) {}

    criar(body: OrcamentoPayload): Observable<CriarOrcamentoResponse> {
        return this.api.post<CriarOrcamentoResponse>(`${this.base}/Criar`, body);
    }

    getBySlug(slug: string) {
        return this.api.get<OrcamentoDetalhe>(`Orcamentos/Public/${slug}`);
    }

    listMine() {
        return this.api.get<OrcamentoListaItem[]>('Orcamentos/Mine');
    }

}
