import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Observable } from 'rxjs';
import { environment } from '../../environments/environment';
import {
    CriarOrcamentoResponse,
    OrcamentoDetalhe,
    OrcamentoListaItem,
    OrcamentoPayload
} from '../models/orcamento.model';

@Injectable({ providedIn: 'root' })
export class OrcamentosService {
    private api = environment.apiUrl + 'Orcamentos';

    constructor(private http: HttpClient) {}

    criar(body: OrcamentoPayload): Observable<CriarOrcamentoResponse> {
        return this.http.post<CriarOrcamentoResponse>(
            `${this.api}/Criar`,
            body,
            { withCredentials: true }
        );
    }

    getBySlug(slug: string): Observable<OrcamentoDetalhe> {
        return this.http.get<OrcamentoDetalhe>(
            `${this.api}/Publico/${encodeURIComponent(slug)}`,
            { withCredentials: true }
        );
    }

    listMine(): Observable<OrcamentoListaItem[]> {
        return this.http.get<OrcamentoListaItem[]>(
            `${this.api}/Meus`,
            { withCredentials: true }
        );
    }
}
