import { ChangeDetectionStrategy, Component } from '@angular/core';
import { CommonModule } from '@angular/common';
import { FormsModule } from '@angular/forms';
import { Router, RouterLink } from '@angular/router';
import { LoginService } from '../../services/login.service';
import { RegistrarUsuarioRequest } from '../../models/usuarios.model';

@Component({
    selector: 'app-register',
    standalone: true,
    imports: [CommonModule, FormsModule, RouterLink],
    templateUrl: './register.component.html',
    styleUrls: ['./register.component.css'],
    changeDetection: ChangeDetectionStrategy.OnPush
})
export class RegisterComponent {
    nomeUsuario = '';
    emailUsuario = '';
    sending = false;
    error = '';

    constructor(private api: LoginService, private router: Router) {}

    private isValidEmail(e: string) {
        return /^\S+@\S+\.\S+$/.test(e.trim().toLowerCase());
    }

    canSubmit(): boolean {
        return !!this.nomeUsuario.trim() && this.isValidEmail(this.emailUsuario);
    }

    onSubmit() {
        if (!this.canSubmit() || this.sending) return;
        this.sending = true;
        this.error = '';

        const body: RegistrarUsuarioRequest = {
            nomeUsuario: this.nomeUsuario.trim(),
            emailUsuario: this.emailUsuario.trim().toLowerCase()
        };

        this.api.RegistrarUsuario(body).subscribe({
            next: () => {
                // Interceptor já mostra toast de sucesso se o backend retornar "mensagem".
                // Após 200, volta ao login:
                this.router.navigate(['/login']);
            },
            error: (err) => {
                this.error = err?.error?.mensagem || 'Falha ao registrar. Tente novamente.';
                this.sending = false;
            }
        });
    }
}
<div class="grid form-shell">
    <div class="card grid" style="gap:16px;">
        <div style="display:flex; align-items:center; justify-content:space-between;">
            <h2 style="margin:0;">Registre-se</h2>
            <div class="step-indicator">Crie sua conta para salvar seus orçamentos</div>
        </div>

        <form class="form-shell" (ngSubmit)="onSubmit()">
            <div>
                <label for="nomeUsuario">Nome de Usuário</label>
                <input class="input" id="nomeUsuario" name="nomeUsuario" [(ngModel)]="nomeUsuario" placeholder="Seu nome" autocomplete="name" required />
            </div>

            <div>
                <label for="emailUsuario">E-mail do Usuário</label>
                <input class="input" type="email" id="emailUsuario" name="emailUsuario" [(ngModel)]="emailUsuario" placeholder="voce@exemplo.com" autocomplete="email" required />
            </div>

            <div *ngIf="error" class="small" style="color:#ef4444;">{{ error }}</div>

            <!-- Lado a lado -->
            <div class="actions">
                <button class="btn btn-primary" type="submit" [disabled]="!canSubmit() || sending">
                    {{ sending ? 'Enviando...' : 'Registrar-se' }}
                </button>
                <a routerLink="/login" class="btn btn-ghost">Já tenho conta</a>
            </div>
        </form>

        <!-- Voltar alinhado logo abaixo -->
        <div class="footer-actions">
            <a routerLink="/novo" class="btn btn-ghost">Voltar</a>
        </div>
    </div>
</div>
