import { ChangeDetectionStrategy, ChangeDetectorRef, Component, OnDestroy } from '@angular/core';
import { CommonModule } from '@angular/common';
import { FormsModule } from '@angular/forms';
import { ActivatedRoute, Router, RouterLink } from '@angular/router';
import { NgxMaskDirective, NgxMaskPipe } from 'ngx-mask';
import { LoginService } from '../../services/login.service';
import {
    PreLoginRequest,
    RetornoDadoGeradoUsuarioResponse,
    RetornoUsuariosGeral
} from '../../models/usuarios.model';
import { AuthService } from '../../services/auth.service';
import { ToastService } from 'src/app/services/toast.service';

@Component({
    selector: 'app-login',
    standalone: true,
    imports: [CommonModule, FormsModule, RouterLink, NgxMaskDirective, NgxMaskPipe],
    templateUrl: './login.component.html',
    styleUrls: ['./login.component.css'],
    changeDetection: ChangeDetectionStrategy.OnPush
})
export class LoginComponent implements OnDestroy {
    keepMeConnected = false;
    email = '';
    codigoGerado = '';
    enviouCodigo = false;
    enviarDesabilitado = false;
    erroLogin = false;
    usaEmail = true;
    tempoRestante = 0;
    private timer: any;
    private dadosUsuario?: RetornoUsuariosGeral;

    constructor(
        private api: LoginService,
        private router: Router,
        private route: ActivatedRoute,
        private auth: AuthService,
        private cdr: ChangeDetectorRef,
        private toast: ToastService
    ) {}

    ngOnDestroy(): void { if (this.timer) clearInterval(this.timer); }

    onSubmit(): void {
        if (!this.enviouCodigo) {
            this.GerarCodigo();
        } else {
            this.LoginUsuario();
        }
    }

    reSend(): void {
        if (this.tempoRestante === 0) {
            this.enviarDesabilitado = false;
            this.enviouCodigo = false; // permite GerarCodigo() rodar de novo
            this.cdr.markForCheck();
            this.GerarCodigo();
        }
    }

    GerarCodigo(): void {
        const body: PreLoginRequest = { email: this.email.trim().toLowerCase() };
        if (!body.email || !/^\S+@\S+\.\S+$/.test(body.email)) { this.erroLogin = true; this.cdr.markForCheck(); return; }

        if (!this.enviarDesabilitado && !this.enviouCodigo) {
            this.enviarDesabilitado = true;
            this.cdr.markForCheck(); // opcional: j√° reflete o disabled
            this.api.PreLogin(body).subscribe({
                next: (resp) => {
                    this.enviouCodigo = true;
                    this.erroLogin = false;
                    this.dadosUsuario = resp.dadosUsuario;

                    const validade = new Date(resp.validade).getTime();
                    const now = Date.now();
                    this.tempoRestante = Math.max(0, Math.floor((validade - now) / 1000));
                    this.startTimer();

                    this.enviarDesabilitado = false;   // libera ‚ÄúConfirmar c√≥digo‚Äù
                    this.cdr.markForCheck();           // üëà for√ßa render imediato dos campos de c√≥digo
                },
                error: () => {
                    this.erroLogin = true;
                    this.enviouCodigo = false;
                    this.enviarDesabilitado = false;
                    if (this.timer) clearInterval(this.timer);
                    this.cdr.markForCheck();           // üëà reflete erro/estado
                }
            });
        }
    }

    private startTimer(): void {
        if (this.timer) clearInterval(this.timer);
        this.timer = setInterval(() => {
            if (this.tempoRestante > 0) {
                this.tempoRestante--;
                this.cdr.markForCheck(); // OnPush: atualiza o contador no DOM
            } else {
                clearInterval(this.timer);
                this.timer = null;
                // üëá expira√ß√£o: volta ao estado natural e avisa
                this.toast.warn('C√≥digo expirou! Tente novamente realizar o login.');
                this.resetForm();        // volta para campo de e-mail + "Enviar c√≥digo"
                this.cdr.markForCheck();
            }
        }, 1000);
    }

    resetForm(): void {
        this.erroLogin = false;
        this.enviouCodigo = false;
        this.enviarDesabilitado = false;
        this.codigoGerado = '';
        this.tempoRestante = 0;
        if (this.timer) clearInterval(this.timer);
        this.cdr.markForCheck();                 // üëà reflete o reset imediatamente
    }

    verificarCodigo(): void {
        this.enviarDesabilitado = !(this.codigoGerado?.toString().length === 6);
    }

    LoginUsuario(): void {
        if (!this.enviarDesabilitado && this.enviouCodigo && this.dadosUsuario) {
            const du = this.dadosUsuario;
            const email = (du.emailUsuario ?? '').trim().toLowerCase();
            const identifier = email;

            this.api.Login(du, this.codigoGerado, this.keepMeConnected).subscribe({
                next: (ok) => {
                    if (ok) {
                        if (this.timer) clearInterval(this.timer);
                        this.erroLogin = false;
                        const email = (du.emailUsuario ?? '').trim().toLowerCase();
                        this.auth.login(identifier, 'otp', du.codigoUsuario);

                        this.toast.show('success', 'Login realizado com sucesso!'); // üëà aqui

                        const redirect = this.route.snapshot.queryParamMap.get('redirect');
                        this.router.navigateByUrl(redirect || '/meus');
                    } else {
                        this.erroLogin = true;
                    }
                },
                error: () => { this.erroLogin = true; }
            });
        }
    }

    formatTime(s: number): string {
        const m = Math.floor(s / 60), r = s % 60;
        return `${m.toString().padStart(2,'0')}:${r.toString().padStart(2,'0')}`;
    }
}
<div class="grid form-shell">
    <div class="card grid" style="gap:16px;">
        <div style="display:flex; align-items:center; justify-content:space-between;">
            <h2 style="margin:0;">Entrar</h2>
            <div class="step-indicator">Acesse para salvar seus or√ßamentos</div>
        </div>

        <!-- FORM √öNICO -->
        <form class="form-shell" (ngSubmit)="onSubmit()">
            <!-- E-MAIL (desabilita quando j√° enviou) -->
            <div>
                <label for="email">E-mail</label>
                <input
                        class="input"
                        type="email"
                        id="email"
                        placeholder="seu-email@exemplo.com"
                        name="email"
                        [(ngModel)]="email"
                        [disabled]="enviouCodigo"
                        autocomplete="email"
                        required
                />
            </div>

            <!-- C√ìDIGO + TIMER (aparece ap√≥s 200) -->
            <div *ngIf="enviouCodigo">
                <label for="codigoGerado">C√≥digo de verifica√ß√£o</label>
                <p class="small">C√≥digo enviado por e‚Äëmail. Expira em {{ formatTime(tempoRestante) }}.</p>
                <input
                        class="input"
                        type="text"
                        id="codigoGerado"
                        [(ngModel)]="codigoGerado"
                        name="codigoGerado"
                        maxlength="6"
                        placeholder="000000"
                        (ngModelChange)="verificarCodigo()"
                        autocomplete="one-time-code"
                        inputmode="numeric"
                        pattern="[0-9]{6}"
                        required
                />
            </div>

            <label *ngIf="enviouCodigo" class="keep">
                <input type="checkbox" [(ngModel)]="keepMeConnected" name="keepMeConnectedValidation">
                Mantenha-me conectado
            </label>

            <!-- A√á√ïES -->
            <div class="form-actions">
                <!-- Bot√£o prim√°rio: muda de r√≥tulo e fun√ß√£o automaticamente -->
                <button
                        class="btn btn-primary"
                        type="submit"
                        [disabled]="enviouCodigo ? (enviarDesabilitado || codigoGerado.length !== 6) : (!email || email.length < 5)"
                >
                    {{ enviouCodigo ? 'Confirmar c√≥digo' : 'Enviar c√≥digo' }}
                </button>

                <!-- Secund√°rios -->
                <button *ngIf="enviouCodigo" type="button" class="btn btn-ghost" (click)="resetForm()">
                    Cancelar
                </button>

                <!-- o bot√£o S√ì existe quando zerar -->
                <button
                        *ngIf="enviouCodigo && tempoRestante === 0"
                        type="button"
                        class="btn btn-ghost"
                        (click)="reSend()"
                >
                    Reenviar c√≥digo
                </button>
            </div>
        </form>

        <!-- ERRO -->
        <div *ngIf="erroLogin" class="card" style="border-color:crimson;">
            <div style="display:flex; gap:8px; align-items:center;">
                <span style="font-size:18px;">‚ö†Ô∏è</span>
                <div>
                    <b>Ops! Algo deu errado</b>
                    <div class="small">Verifique seus dados e tente novamente</div>
                </div>
            </div>
            <div style="margin-top:8px;">
                <button class="btn btn-ghost" (click)="resetForm()">Tentar novamente</button>
            </div>
        </div>

        <!-- A√á√ïES SECUND√ÅRIAS (fora do form) -->
        <div style="display:flex; gap:8px; align-items:center; justify-content:space-between; flex-wrap:wrap;">
            <div style="display:flex; gap:8px; align-items:center;">
                <a routerLink="/novo" class="btn btn-ghost">Continuar sem login</a>
                <a *ngIf="!enviouCodigo" routerLink="/registrar" class="btn btn-ghost">Registre-se</a>
            </div>
        </div>
    </div>
</div>
