import { Component, EventEmitter, Input, Output, ChangeDetectionStrategy } from '@angular/core';
import { CommonModule } from '@angular/common';

@Component({
  selector: 'app-photo-uploader',
  standalone: true,
  imports: [CommonModule],
  templateUrl: './photo-uploader.component.html',
  styleUrls: ['./photo-uploader.component.css'],
  changeDetection: ChangeDetectionStrategy.OnPush,
})
export class PhotoUploaderComponent {
  /** valor externo ([(value)]) — array de dataURLs/URLs */
  @Input() value: string[] = [];
  @Output() valueChange = new EventEmitter<string[]>();

  /** máx. de fotos */
  @Input() max = 5;

  /** abrir seletor de arquivos */
  openFile(input: HTMLInputElement) { input.click(); }

  /** ao selecionar arquivos */
  async onFilesSelected(ev: Event) {
    const input = ev.target as HTMLInputElement;
    const files = input.files ? Array.from(input.files) : [];
    if (!files.length) return;

    // filtra imagens e respeita o limite
    const spaceLeft = Math.max(0, this.max - this.value.length);
    const toRead = files
        .filter(f => f.type.startsWith('image/'))
        .slice(0, spaceLeft);

    const dataUrls = await Promise.all(toRead.map(f => this.readAsDataURL(f)));
    const next = [...this.value, ...dataUrls];
    this.value = next;
    this.valueChange.emit(next);

    // reseta input pra permitir re-selecionar o mesmo arquivo
    input.value = '';
  }

  /** remove uma foto pelo índice */
  removeAt(i: number) {
    const next = this.value.filter((_, idx) => idx !== i);
    this.value = next;
    this.valueChange.emit(next);
  }

  /** limpar tudo */
  clearAll() {
    this.value = [];
    this.valueChange.emit([]);
  }

  private readAsDataURL(file: File): Promise<string> {
    return new Promise((resolve, reject) => {
      const reader = new FileReader();
      reader.onerror = () => reject(reader.error);
      reader.onload = () => resolve(String(reader.result));
      reader.readAsDataURL(file);
    });
  }
}
<div class="uploader">
    <!-- grade de thumbs -->
    <div class="thumb-grid">
        <div class="thumb" *ngFor="let url of value; let i = index">
            <img [src]="url" alt="foto" />
            <button type="button" class="thumb-remove" (click)="removeAt(i)" aria-label="Remover">✕</button>
        </div>

        <!-- “slot vazio” opcional apenas para manter a grade uniforme -->
        <div class="thumb placeholder" *ngFor="let _ of [].constructor(Math.max(0, max - value.length)) | slice:0:0"></div>
    </div>

    <!-- ações fixas no rodapé -->
    <div class="actions">
        <input #fileInput type="file" multiple accept="image/*" (change)="onFilesSelected($event)" hidden />
        <button type="button" class="btn-photo" (click)="openFile(fileInput)">
            ADICIONAR FOTOS ({{value.length}}/{{max}})
        </button>
        <button type="button" class="btn-photo ghost" (click)="clearAll()" [disabled]="!value.length">
            Limpar fotos
        </button>
    </div>
</div>
