import { Component, EventEmitter, Input, Output } from '@angular/core';
import { CommonModule } from '@angular/common';

@Component({
  selector: 'app-photo-uploader',
  standalone: true,
  imports: [CommonModule],
  templateUrl: './photo-uploader.component.html',
  styleUrls: ['./photo-uploader.component.css']
})
export class PhotoUploaderComponent {
  @Input() value: string[] = [];
  @Input() max = 5;
  @Output() valueChange = new EventEmitter<string[]>();

  async onFileChange(ev: Event) {
    const input = ev.target as HTMLInputElement;
    if (!input.files) return;
    const files = Array.from(input.files);
    for (const f of files) {
      if (this.value.length >= this.max) break;
      if (!/^image\/(png|jpe?g|webp)$/i.test(f.type)) continue;
      const dataUrl = await this.readAsDataURL(f);
      this.value = [...this.value, dataUrl];
    }
    this.valueChange.emit(this.value);
    input.value = '';
  }

  removeAt(i: number) {
    this.value.splice(i,1);
    this.valueChange.emit([...this.value]);
  }

  clear() { this.value = []; this.valueChange.emit(this.value); }

  private readAsDataURL(file: File) {
    return new Promise<string>((resolve, reject) => {
      const reader = new FileReader();
      reader.onload = () => resolve(reader.result as string);
      reader.onerror = reject;
      reader.readAsDataURL(file);
    });
  }
}
<div class="photos">
  <div *ngFor="let p of value; index as i" class="photo-wrap">
    <img class="photo" [src]="p" alt="foto {{i+1}}" />
    <button type="button" class="remove btn btn-ghost" (click)="removeAt(i)">×</button>
  </div>
  <label class="upload btn btn-ghost">
    <input type="file" accept="image/png,image/jpeg,image/webp" (change)="onFileChange($event)" multiple [disabled]="value.length>=max" />
    <span>Adicionar fotos ({{value.length}}/{{max}})</span>
  </label>
  <button type="button" class="btn btn-ghost" (click)="clear()">Limpar fotos</button>
</div>
