import { ChangeDetectionStrategy, Component, OnDestroy } from '@angular/core';
import { CommonModule } from '@angular/common';
import { FormsModule } from '@angular/forms';
import { Router, RouterLink } from '@angular/router';
import { LoginService } from '../../services/login.service';
import {
    PreLoginRequest,
    RetornoDadoGeradoUsuarioResponse,
    RetornoUsuariosGeral
} from '../../models/usuarios.model';
import { AuthService } from '../../services/auth.service';

@Component({
    selector: 'app-login',
    standalone: true,
    imports: [CommonModule, FormsModule, RouterLink],
    templateUrl: './login.component.html',
    styleUrls: ['./login.component.css'],
    changeDetection: ChangeDetectionStrategy.OnPush
})
export class LoginComponent implements OnDestroy {
    temaEscuroAtivo = document.documentElement.getAttribute('data-theme') === 'dark';
    keepMeConnected = false;
    telefone = '';
    email = '';
    codigoGerado = '';
    enviouCodigo = false;
    enviarDesabilitado = false;
    erroLogin = false;
    usaEmail = true;
    tempoRestante = 0;
    private timer: any;
    private dadosUsuario?: RetornoUsuariosGeral;

    constructor(
        private api: LoginService,
        private router: Router,
        private auth: AuthService
    ) {}

    ngOnDestroy(): void {
        if (this.timer) clearInterval(this.timer);
    }

    alternarTema(): void {
        this.temaEscuroAtivo = !this.temaEscuroAtivo;
        const t = this.temaEscuroAtivo ? 'dark' : 'light';
        document.documentElement.setAttribute('data-theme', t);
        localStorage.setItem('theme', t);
    }

    utilizaEmail(): void { this.usaEmail = !this.usaEmail; }

    GerarCodigo(): void {
        const body: PreLoginRequest = { email: this.usaEmail ? this.email : undefined, telefone: this.usaEmail ? undefined : this.telefone };

        if (!this.enviarDesabilitado && !this.enviouCodigo) {
            this.enviarDesabilitado = true;
            this.api.PreLogin(body).subscribe({
                next: (resp: RetornoDadoGeradoUsuarioResponse) => {
                    this.enviouCodigo = true;
                    this.erroLogin = false;
                    this.dadosUsuario = resp.dadosUsuario;

                    const validade = new Date(resp.validade).getTime();
                    const now = Date.now();
                    this.tempoRestante = Math.max(0, Math.floor((validade - now) / 1000));
                    this.startTimer();
                },
                error: () => {
                    this.erroLogin = true;
                    this.enviouCodigo = false;
                    this.enviarDesabilitado = false;
                    if (this.timer) clearInterval(this.timer);
                }
            });
        }
    }

    private startTimer(): void {
        if (this.timer) clearInterval(this.timer);
        this.timer = setInterval(() => {
            if (this.tempoRestante > 0) {
                this.tempoRestante--;
            } else {
                this.resetForm();
            }
        }, 1000);
    }

    verificarCodigo(): void {
        this.enviarDesabilitado = !(this.codigoGerado?.toString().length === 6);
    }

    LoginUsuario(): void {
        if (!this.enviarDesabilitado && this.enviouCodigo && this.dadosUsuario) {
            if (this.dadosUsuario.emailUsuario === null) this.dadosUsuario.emailUsuario = '';
            this.api.Login(this.dadosUsuario, this.codigoGerado, this.keepMeConnected).subscribe({
                next: (ok) => {
                    if (ok) {
                        if (this.timer) clearInterval(this.timer);
                        this.erroLogin = false;
                        // marca sess√£o local do nosso app:
                        this.auth.login(this.dadosUsuario.emailUsuario || `tel:${this.dadosUsuario.telefoneUsuario || ''}`, 'otp');
                        this.router.navigate(['/meus']);
                    } else {
                        this.erroLogin = true;
                    }
                },
                error: () => {
                    this.erroLogin = true;
                }
            });
        }
    }

    resetForm(): void {
        this.erroLogin = false;
        this.enviouCodigo = false;
        this.enviarDesabilitado = false;
        this.telefone = '';
        this.codigoGerado = '';
        this.tempoRestante = 0;
        if (this.timer) clearInterval(this.timer);
    }

    formatTime(s: number): string {
        const m = Math.floor(s / 60);
        const r = s % 60;
        return `${m.toString().padStart(2,'0')}:${r.toString().padStart(2,'0')}`;
    }
}
