import { Component, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common';
import { ActivatedRoute, RouterLink } from '@angular/router';
import { Quote } from '../../models';
import { QuoteView } from 'src/app/models/orcamento.model';
import { QuoteService } from 'src/app/services/quote.service';

@Component({
  standalone: true,
  imports: [CommonModule, RouterLink],
  selector: 'app-result',
  templateUrl: './result.component.html',
  styleUrls: ['./result.component.css']
})
export class ResultComponent implements OnInit {
  quote: Quote | null = null;
  link = '';
  slug = '';
  data?: QuoteView;
  loading = true;

  constructor(private route: ActivatedRoute, private quotes: QuoteService) {
    const slug = this.route.snapshot.paramMap.get('slug')!;
    this.link = location.origin + '/o/' + slug;
  }

  ngOnInit(): void {
    this.slug = this.route.snapshot.paramMap.get('slug') || '';
    if (!this.slug) { this.loading = false; return; }

    this.quotes.getBySlug(this.slug).subscribe({
      next: (q) => { this.data = q; this.loading = false; },
      error: () => { this.loading = false; }
    });
  }

  copy() { navigator.clipboard.writeText(this.link); alert('Link copiado!'); }
}
<ng-container *ngIf="quote; else notFound">
  <div class="grid form-shell">
    <div class="card grid" style="gap:16px;">
      <div style="display:flex; align-items:center; justify-content:space-between; gap:12px;">
        <div>
          <div class="small">Orçamento • {{quote.createdAt | date:'short'}}</div>
          <h2 style="margin:0;">{{quote.category}} — {{quote.model}}</h2>
        </div>
        <a routerLink="/novo" class="btn btn-ghost">Novo orçamento</a>
      </div>

      <div class="form-shell">
        <div class="card">
          <div class="small">Faixa sugerida</div>
          <div class="price">R$ {{quote.priceMin}} – R$ {{quote.priceMax}}</div>
        </div>
        <div class="card">
          <div class="small">Valor escolhido</div>
          <div class="price">R$ {{quote.chosenPrice}}</div>
        </div>
      </div>

      <div class="form-shell">
        <div class="card">
          <div class="small">Média do novo</div>
          <div class="price">R$ {{quote.newPriceAvg}}</div>
          <div class="small">Fontes:</div>
          <ul class="small"><li *ngFor="let s of quote.newPriceSources">{{s}}</li></ul>
        </div>
        <div class="card">
          <div class="small">Média do usado</div>
          <div class="price">R$ {{quote.usedPriceAvg}}</div>
          <div class="small">Fontes:</div>
          <ul class="small"><li *ngFor="let s of quote.usedPriceSources">{{s}}</li></ul>
        </div>
      </div>

      <div>
        <div class="small">Fotos</div>
        <div class="photos" *ngIf="quote.photos?.length; else noPhotos">
          <img *ngFor="let p of quote.photos" class="photo" [src]="p" alt="foto" />
        </div>
        <ng-template #noPhotos><div class="small">Sem fotos</div></ng-template>
      </div>

      <div class="card">
        <div class="small">Link público</div>
        <div style="display:flex; gap:8px; align-items:center; flex-wrap:wrap;">
          <code style="background:color-mix(in oklab, var(--bg-2) 60%, transparent); border:1px solid var(--border); padding:8px 10px; border-radius:8px;">{{link}}</code>
          <button class="btn btn-ghost" (click)="copy()">Copiar</button>
          <a class="btn btn-primary" [href]="'/o/' + (quote?.slug || '')">Abrir</a>
        </div>
      </div>
    </div>
  </div>
</ng-container>
<ng-template #notFound><div class="card form-shell">Orçamento não encontrado.</div></ng-template>
