import { Component } from '@angular/core';
import { CommonModule } from '@angular/common';
import { RouterLink } from '@angular/router';
import { QuoteService } from '../../services/quote.service';
import { Quote } from '../../models';

@Component({
  standalone: true,
  selector: 'app-list',
  imports: [CommonModule, RouterLink],
  templateUrl: './list.component.html',
  styleUrls: ['./list.component.css']
})
export class ListComponent {
  items: Quote[] = [];
  constructor(private svc: QuoteService) { this.items = this.svc.all(); }
}
<div class="grid form-shell">
  <div class="card grid" style="gap:12px;">
    <h2 style="margin:0;">Meus Orçamentos</h2>
    <div *ngIf="!items.length" class="small">Você ainda não tem orçamentos salvos.</div>
    <div class="grid" style="gap:8px;">
      <div *ngFor="let q of items" class="card" style="display:flex; align-items:center; justify-content:space-between; gap:12px;">
        <div>
          <div class="small">{{q.createdAt | date:'short'}}</div>
          <div><b>{{q.category}}</b> — {{q.model}}</div>
          <div class="small">R$ {{q.priceMin}} – R$ {{q.priceMax}} • Escolhido: R$ {{q.chosenPrice}}</div>
        </div>
        <div style="display:flex; gap:8px;">
          <a [routerLink]="['/resultado', q.slug]" class="btn btn-ghost">Abrir</a>
          <a [routerLink]="['/o', q.slug]" class="btn btn-primary">Público</a>
        </div>
      </div>
    </div>
  </div>
</div>
