import { Component, EventEmitter, OnInit, Output } from '@angular/core';
import { CommonModule } from '@angular/common';
import { FormBuilder, ReactiveFormsModule, Validators } from '@angular/forms';
import { HttpClient, HttpClientModule } from '@angular/common/http';
import { catchError, debounceTime, distinctUntilChanged, filter, map, of, switchMap, tap } from 'rxjs';
import { FreteQuote, FreteRequest } from 'src/app/models/orcamento.model';

@Component({
    standalone: true,
    selector: 'app-step9-custos-adicionais',
    imports: [CommonModule, ReactiveFormsModule, HttpClientModule],
    templateUrl: './step9-custos-adicionais.component.html',
    styleUrls: ['./step9-custos-adicionais.component.css']
})
export class Step9CustosAdicionaisComponent implements OnInit {
    @Output() changed = new EventEmitter<{
        nomeCompleto: string;
        incluirTransporte: boolean;
        custoTransporte?: number;
        frete?: FreteQuote;
    }>();

    constructor(private fb: FormBuilder, private http: HttpClient) {}

    form = this.fb.group({
        nomeCompleto: ['', [Validators.required, Validators.minLength(3)]],
        incluirTransporte: [false],
    });

    transporteForm = this.fb.group({
        // ORIGEM
        origemCep: ['', [Validators.required, Validators.minLength(8)]],
        origemUf: [{ value: '', disabled: true }, [Validators.required, Validators.minLength(2), Validators.maxLength(2)]],
        origemCidade: [{ value: '', disabled: true }, [Validators.required, Validators.minLength(2)]],
        // DESTINO
        destinoCep: ['', [Validators.required, Validators.minLength(8)]],
        destinoUf: [{ value: '', disabled: true }, [Validators.required, Validators.minLength(2), Validators.maxLength(2)]],
        destinoCidade: [{ value: '', disabled: true }, [Validators.required, Validators.minLength(2)]],
    });

    modalOpen = false;
    loading = false;
    frete?: FreteQuote;

    // status de CEP
    buscandoOrigem = false;
    buscandoDestino = false;
    cepOrigemErro = '';
    cepDestinoErro = '';

    ngOnInit(): void {
        // watchers de CEP (8 dígitos) → ViaCEP → preenche UF/Cidade
        this.watchCep(
            'origemCep', 'origemUf', 'origemCidade',
            (b) => (this.buscandoOrigem = b),
            (e) => (this.cepOrigemErro = e)
        );

        this.watchCep(
            'destinoCep', 'destinoUf', 'destinoCidade',
            (b) => (this.buscandoDestino = b),
            (e) => (this.cepDestinoErro = e)
        );
    }

    // ==== CEP helpers ====
    private watchCep(
        ctrlCep: 'origemCep' | 'destinoCep',
        ctrlUf: 'origemUf' | 'destinoUf',
        ctrlCidade: 'origemCidade' | 'destinoCidade',
        setBusy: (b: boolean) => void,
        setErr: (msg: string) => void
    ) {
        const cepCtrl = this.transporteForm.controls[ctrlCep];
        cepCtrl.valueChanges!.pipe(
            map(v => (v || '').toString().replace(/\D/g, '')),          // só dígitos
            tap(v => { if (v !== cepCtrl.value) cepCtrl.setValue(v, { emitEvent: false }); }),
            debounceTime(250),
            distinctUntilChanged(),
            tap(() => { setErr(''); }),
            filter(v => v.length >= 8),                                  // dispara com 8+ dígitos
            tap(() => setBusy(true)),
            switchMap(cep =>
                this.http.get<any>(`https://viacep.com.br/ws/${cep}/json/`).pipe(
                    catchError(() => of(null)),
                    tap(() => setBusy(false))
                )
            )
        ).subscribe(resp => {
            if (!resp || resp.erro) {
                // limpa campos e mostra erro
                this.transporteForm.patchValue({ [ctrlUf]: '', [ctrlCidade]: '' } as any, { emitEvent: false });
                setErr('CEP não encontrado');
                return;
            }
            // preenche
            this.transporteForm.patchValue(
                { [ctrlUf]: (resp.uf || '').toUpperCase(), [ctrlCidade]: resp.localidade || '' } as any,
                { emitEvent: false }
            );
            setErr('');
        });
    }

    // ==== UI principal ====
    onToggleTransporte() {
        const marcado = this.form.value.incluirTransporte === true;
        if (marcado) this.openModal();
        this.emit();
    }
    openModal() { this.modalOpen = true; }
    closeModal() { this.modalOpen = false; }

    async calcularFrete() {
        if (this.transporteForm.invalid) return;
        this.loading = true;
        try {
            const body: FreteRequest = {
                origem: {
                    cep: this.transporteForm.value.origemCep!.trim(),
                    uf: (this.transporteForm.getRawValue().origemUf || '').toString().toUpperCase(),
                    cidade: this.transporteForm.getRawValue().origemCidade || '',
                },
                destino: {
                    cep: this.transporteForm.value.destinoCep!.trim(),
                    uf: (this.transporteForm.getRawValue().destinoUf || '').toString().toUpperCase(),
                    cidade: this.transporteForm.getRawValue().destinoCidade || '',
                },
                tipoCarga: 'carga_geral',
                eixos: 2,
                retornoVazio: false,
            };

            const res = await fetch('/Orcamento/CalcularFrete', {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                credentials: 'include',
                body: JSON.stringify(body),
            });
            if (!res.ok) throw new Error('Falha no cálculo de frete');
            this.frete = await res.json() as FreteQuote;
        } catch {
            this.frete = undefined;
            alert('Não foi possível calcular o frete agora.');
        } finally {
            this.loading = false;
        }
    }

    aplicarFrete() {
        if (!this.frete) return;
        this.modalOpen = false;
        this.emit();
    }

    emit() {
        this.changed.emit({
            nomeCompleto: (this.form.value.nomeCompleto || '').trim(),
            incluirTransporte: this.form.value.incluirTransporte === true,
            custoTransporte: this.frete?.valor,
            frete: this.frete,
        });
    }
}
