import { Component, EventEmitter, OnInit, Output } from '@angular/core';
import { CommonModule } from '@angular/common';
import { AbstractControl, FormBuilder, FormGroup, ReactiveFormsModule, Validators } from '@angular/forms';
import { HttpClient, HttpClientModule, HttpBackend } from '@angular/common/http';
import { catchError, debounceTime, distinctUntilChanged, filter, map, of, switchMap, tap } from 'rxjs';
import { FreteQuote } from 'src/app/models/orcamento.model';
import { OrcamentosService } from 'src/app/services/orcamentos.service';
import { NgxMaskDirective } from 'ngx-mask';

@Component({
    standalone: true,
    selector: 'app-step9-custos-adicionais',
    imports: [CommonModule, ReactiveFormsModule, HttpClientModule, NgxMaskDirective],
    templateUrl: './passo9-custos-adicionais.component.html',
    styleUrls: ['./passo9-custos-adicionais.component.css']
})
export class Step9CustosAdicionaisComponent implements OnInit {
    @Output() changed = new EventEmitter<{
        nomeCompleto: string;
        incluirTransporte: boolean;
        custoTransporte?: number;
        frete?: FreteQuote;
    }>();

    constructor(
        private fb: FormBuilder,
        httpBackend: HttpBackend,
        private orcSvc: OrcamentosService
    ) { this.httpRaw = new HttpClient(httpBackend); }

    private httpRaw: HttpClient;

    readonly MAX_PESO = 30; // limite para cálculo automático
    readonly DEFAULT_PKG = { height: 5, width: 15, length: 20 }; // placeholders & fallback

    freteFonte = 'SuperFrete (PAC/SEDEX)';

    form = this.fb.group({
        nomeCompleto: ['', [Validators.required, Validators.minLength(3)]],
        incluirTransporte: [false],
    });

    transporteForm: FormGroup = this.fb.group({
        // ORIGEM
        origemCep: ['', [Validators.required, Validators.minLength(8)]],
        origemUf: [{ value: '', disabled: true }, [Validators.required, Validators.minLength(2), Validators.maxLength(2)]],
        origemCidade: [{ value: '', disabled: true }, [Validators.required, Validators.minLength(2)]],
        // DESTINO
        destinoCep: ['', [Validators.required, Validators.minLength(8)]],
        destinoUf: [{ value: '', disabled: true }, [Validators.required, Validators.minLength(2), Validators.maxLength(2)]],
        destinoCidade: [{ value: '', disabled: true }, [Validators.required, Validators.minLength(2)]],
        // CAMPOS DE CÁLCULO
        pesoKg: [null as number | null, [Validators.required, Validators.min(0.3)]],
        // DIMENSÕES (opcionais) — se vazias, uso DEFAULT_PKG; se presentes, validar limites
        alturaCm: [null as number | null, [Validators.min(1), Validators.max(150)]],
        larguraCm: [null as number | null, [Validators.min(1), Validators.max(150)]],
        comprimentoCm: [null as number | null, [Validators.min(1), Validators.max(150)]],
        // VALOR MANUAL (fora da modal)
        valorManual: [null as number | null]
    }, { validators: [dimsSumValidator(200)] }); // <<< valida soma C+L+A

    // estado UI
    modalOpen = false;
    loading = false;
    frete?: FreteQuote;

    // status CEP
    buscandoOrigem = false; buscandoDestino = false;
    cepOrigemErro = ''; cepDestinoErro = '';
    apiError = '';

    // hints de "?"
    hint = { altura: false, largura: false, comprimento: false };

    ngOnInit(): void {
        // CEP watchers
        this.watchCep('origemCep', 'origemUf', 'origemCidade', b => this.buscandoOrigem = b, e => this.cepOrigemErro = e);
        this.watchCep('destinoCep', 'destinoUf', 'destinoCidade', b => this.buscandoDestino = b, e => this.cepDestinoErro = e);

        // emite quando o VALOR MANUAL muda
        this.transporteForm.controls['valorManual'].valueChanges!
            .pipe(debounceTime(150))
            .subscribe(() => this.emit());

        // emite quando o NOME COMPLETO muda
        this.form.controls['nomeCompleto'].valueChanges!
            .pipe(debounceTime(150))
            .subscribe(() => this.emit());
    }

    // ======= Helpers de validação =======
    get dimsSumExceeded(): boolean {
        return !!this.transporteForm.errors?.['dimsSumExceeded'];
    }

    toggleHint(k: 'altura' | 'largura' | 'comprimento') {
        this.hint[k] = !this.hint[k];
    }

    // CEP → ViaCEP
    private watchCep(
        ctrlCep: 'origemCep' | 'destinoCep', ctrlUf: 'origemUf' | 'destinoUf', ctrlCidade: 'origemCidade' | 'destinoCidade',
        setBusy: (b: boolean) => void, setErr: (m: string) => void
    ) {
        const cepCtrl = this.transporteForm.controls[ctrlCep] as AbstractControl;
        cepCtrl.valueChanges!.pipe(
            map((v: any) => (v || '').toString().replace(/\D/g, '')),
            tap(v => { if (v !== cepCtrl.value) cepCtrl.setValue(v, { emitEvent: false }); }),
            debounceTime(250),
            distinctUntilChanged(),
            tap(() => setErr('')),
            filter(v => v.length >= 8),
            tap(() => setBusy(true)),
            switchMap(cep => this.httpRaw.get<any>(`https://viacep.com.br/ws/${cep}/json/`, { withCredentials: false }).pipe(
                catchError(() => of(null)),
                tap(() => setBusy(false))
            ))
        ).subscribe(resp => {
            if (!resp || resp.erro) {
                this.transporteForm.patchValue({ [ctrlUf]: '', [ctrlCidade]: '' } as any, { emitEvent: false });
                setErr('CEP não encontrado');
                return;
            }
            this.transporteForm.patchValue(
                { [ctrlUf]: (resp.uf || '').toUpperCase(), [ctrlCidade]: resp.localidade || '' } as any,
                { emitEvent: false }
            );
            setErr('');
        });
    }

    // ======= UI principal =======
    onToggleTransporte() {
        // não abre modal automaticamente
        this.emit();
    }

    openModal() { this.modalOpen = true; this.apiError = ''; }
    closeModal() { this.modalOpen = false; }

    // peso > 30 bloqueia cálculo
    get pesoAcimaLimite(): boolean {
        const v = Number(this.transporteForm.controls['pesoKg'].value ?? 0);
        return v > this.MAX_PESO;
    }

    onPesoInput() {
        const ctrl = this.transporteForm.controls['pesoKg'];
        const v = Number(ctrl.value ?? 0);
        const errs = { ...(ctrl.errors || {}) };
        if (v > this.MAX_PESO) errs['limite'] = true; else delete errs['limite'];
        ctrl.setErrors(Object.keys(errs).length ? errs : null);
    }

    calcularFrete() {
        this.apiError = '';

        if (this.pesoAcimaLimite) {
            this.apiError = `Peso acima do limite de ${this.MAX_PESO} kg para cálculo automático. Informe o valor manualmente.`;
            return;
        }
        if (this.dimsSumExceeded) {
            this.apiError = 'A soma (Comprimento + Largura + Altura) não pode exceder 200 cm.';
            return;
        }
        if (this.transporteForm.invalid) return;

        this.loading = true;
        const raw = this.transporteForm.getRawValue();

        // fallback de dimensões aos padrões se usuário não preencher
        const height = this.numOrDefault(raw.alturaCm, this.DEFAULT_PKG.height);
        const width  = this.numOrDefault(raw.larguraCm, this.DEFAULT_PKG.width);
        const length = this.numOrDefault(raw.comprimentoCm, this.DEFAULT_PKG.length);

        const body: any = {
            origem: { cep: (raw.origemCep || '').trim(), uf: (raw.origemUf || '').toString().toUpperCase(), cidade: raw.origemCidade || '' },
            destino:{ cep: (raw.destinoCep|| '').trim(), uf: (raw.destinoUf|| '').toString().toUpperCase(), cidade: raw.destinoCidade|| '' },
            pesoKg: this.pesoAcimaLimite ? undefined : Number(raw.pesoKg), // segurança extra
            retornoVazio: false,
            package: { height, width, length }
        };

        this.orcSvc.calcularFrete(body).subscribe({
            next: (q: FreteQuote) => { this.frete = q; this.loading = false; this.emit(); },
            error: (err) => {
                this.frete = undefined;
                this.apiError = err?.error?.mensagem || 'Falha ao calcular frete.';
                this.loading = false;
                this.emit();
            }
        });
    }

    aplicarFrete() {
        if (!this.frete) return;
        this.modalOpen = false;
        this.emit();
    }

    limparFreteForm() {
        this.apiError = '';
        this.frete = undefined;
        this.buscandoOrigem = this.buscandoDestino = false;
        this.cepOrigemErro = this.cepDestinoErro = '';

        this.transporteForm.reset({
            origemCep: '',
            origemUf: '',
            origemCidade: '',
            destinoCep: '',
            destinoUf: '',
            destinoCidade: '',
            pesoKg: null,
            alturaCm: null,
            larguraCm: null,
            comprimentoCm: null,
            valorManual: this.transporteForm.controls['valorManual'].value
        }, { emitEvent: false });

        // limpa erros
        ['pesoKg','alturaCm','larguraCm','comprimentoCm'].forEach(k => {
            const c = this.transporteForm.controls[k];
            c.setErrors(null);
            c.markAsPristine();
            c.markAsUntouched();
        });

        this.emit();
    }

    // Helpers
    get valorManual(): number | null {
        const v = this.transporteForm.value['valorManual'] as any;
        if (v === null || v === undefined || v === '') return null;
        const n = Number(v);
        return isNaN(n) ? null : n;
    }

    private custoSelecionado(): number | undefined {
        if (this.form.value.incluirTransporte !== true) return undefined;
        if (this.valorManual && this.valorManual > 0) return this.valorManual;
        return this.frete?.valor;
    }

    private numOrDefault(v: any, d: number): number {
        const n = Number(v);
        return isNaN(n) || n <= 0 ? d : n;
    }

    emit() {
        this.changed.emit({
            nomeCompleto: (this.form.value.nomeCompleto || '').trim(),
            incluirTransporte: this.form.value.incluirTransporte === true,
            custoTransporte: this.custoSelecionado(),
            frete: this.frete,
        });
    }
}

/** Validator de grupo: soma (C + L + A) <= maxSum.
 *  Só valida quando os 3 valores estão presentes e > 0 (se algum estiver vazio, não acusa erro).
 */
function dimsSumValidator(maxSum: number) {
    return (ac: AbstractControl) => {
        const g = ac as FormGroup;
        const h = Number(g.get('alturaCm')?.value ?? 0);
        const w = Number(g.get('larguraCm')?.value ?? 0);
        const l = Number(g.get('comprimentoCm')?.value ?? 0);

        // se não informou todos, não bloqueia por soma
        if (!(h > 0 && w > 0 && l > 0)) return null;

        return (h + w + l) > maxSum ? { dimsSumExceeded: true } : null;
    };
}
<!-- CARD: Custos adicionais -->
<div class="card grid" style="gap:12px;">
    <h3 class="title">Custos adicionais</h3>

    <!-- TRANSPORTE -->
    <label class="row">
        <input
                type="checkbox"
                [formControl]="form.controls.incluirTransporte"
                (change)="onToggleTransporte()" />
        <span>Transporte</span>

        <!-- Botão só aparece quando marcado -->
        <button
                class="btn btn-ghost sm push-right"
                type="button"
                *ngIf="form.value.incluirTransporte"
                (click)="openModal()">
            Configurar / calcular
        </button>
    </label>

    <!-- Campo de valor manual (currency) quando marcado -->
    <div class="manual" *ngIf="form.value.incluirTransporte">
        <label class="col">
            <span class="small">Valor de transporte (opcional)</span>
            <input
                    class="input"
                    type="text"
                    inputmode="numeric"
                    [mask]="'separator.2'"
                    [thousandSeparator]="'.'"
                    [prefix]="'R$ '"
                    [dropSpecialCharacters]="true"
                    [allowNegativeNumbers]="false"
                    [formControl]="transporteForm.controls.valorManual"
                    placeholder="R$ 0,00" />
            <span class="field-hint small">Se preencher, <b>substitui</b> o valor calculado.</span>
        </label>
    </div>

    <!-- Resumo/seleção -->
    <div class="resumo" *ngIf="form.value.incluirTransporte">
        <div class="small">Transporte</div>
        <ng-container *ngIf="valorManual && valorManual > 0; else calcado">
            <div class="pill">Valor (manual): {{ valorManual | currency:'BRL':'symbol':'1.2-2' }}</div>
        </ng-container>
        <ng-template #calcado>
            <div class="pill" *ngIf="frete; else aguardando">
                Valor (calculado): {{ frete?.valor | currency:'BRL':'symbol':'1.2-2' }}
            </div>
            <ng-template #aguardando><div class="pill muted">Aguardando cálculo…</div></ng-template>
        </ng-template>
    </div>
</div>

<!-- CARD: Nome completo (separado) -->
<div class="card grid" style="gap:12px;">
    <h3 class="title">Nome completo</h3>
    <label class="col">
        <input
                type="text"
                class="input"
                placeholder="Digite seu nome"
                [formControl]="form.controls.nomeCompleto" />
        <span class="field-hint" *ngIf="form.controls.nomeCompleto.invalid && form.controls.nomeCompleto.touched">
      Informe seu nome completo.
    </span>
    </label>
</div>

<!-- MODAL -->
<div class="modal" *ngIf="modalOpen" (click)="closeModal()">
    <div class="modal-inner" (click)="$event.stopPropagation()">
        <div class="modal-head">
            <h4>Calculadora de frete</h4>
            <button class="btn btn-ghost" (click)="closeModal()">×</button>
        </div>

        <div class="grid two">
            <!-- ORIGEM -->
            <div class="card">
                <div class="small muted">Endereço de origem</div>
                <div class="grid three">
                    <label>
                        <span class="small">CEP</span>
                        <input
                                class="input"
                                inputmode="numeric"
                                placeholder="00000000"
                                [formControl]="transporteForm.controls.origemCep" />
                        <div class="hint-row">
                            <span class="small muted" *ngIf="buscandoOrigem">Buscando CEP…</span>
                            <span class="small error" *ngIf="cepOrigemErro">{{ cepOrigemErro }}</span>
                        </div>
                    </label>
                    <label>
                        <span class="small">UF</span>
                        <input
                                class="input readonly"
                                placeholder="—"
                                [formControl]="transporteForm.controls.origemUf" />
                    </label>
                    <label>
                        <span class="small">Cidade</span>
                        <input
                                class="input readonly"
                                placeholder="—"
                                [formControl]="transporteForm.controls.origemCidade" />
                    </label>
                </div>
            </div>

            <!-- DESTINO -->
            <div class="card">
                <div class="small muted">Endereço de destino</div>
                <div class="grid three">
                    <label>
                        <span class="small">CEP</span>
                        <input
                                class="input"
                                inputmode="numeric"
                                placeholder="00000000"
                                [formControl]="transporteForm.controls.destinoCep" />
                        <div class="hint-row">
                            <span class="small muted" *ngIf="buscandoDestino">Buscando CEP…</span>
                            <span class="small error" *ngIf="cepDestinoErro">{{ cepDestinoErro }}</span>
                        </div>
                    </label>
                    <label>
                        <span class="small">UF</span>
                        <input
                                class="input readonly"
                                placeholder="—"
                                [formControl]="transporteForm.controls.destinoUf" />
                    </label>
                    <label>
                        <span class="small">Cidade</span>
                        <input
                                class="input readonly"
                                placeholder="—"
                                [formControl]="transporteForm.controls.destinoCidade" />
                    </label>
                </div>
            </div>
        </div>

        <!-- PESO + DIMENSÕES -->
        <div class="grid three" style="margin-top:12px;">
            <!-- Peso -->
            <label>
                <span class="small">Peso (kg)</span>
                <input
                        class="input"
                        type="number"
                        min="0.3"
                        [max]="MAX_PESO"
                        step="0.1"
                        placeholder="Ex.: 1.5"
                        [formControl]="transporteForm.controls.pesoKg"
                        (input)="onPesoInput()" />
                <div class="hint-row">
          <span class="small error" *ngIf="pesoAcimaLimite">
            O limite para cálculo automático é {{ MAX_PESO }} kg. Informe manualmente o valor do transporte fora desta janela.
          </span>
                    <span class="small muted" *ngIf="!pesoAcimaLimite">
            * Correios normalmente aceitam ~0,3 a 30 kg.
          </span>
                </div>
            </label>

            <!-- Altura -->
            <label>
        <span class="small">
          Altura (cm)
          <button type="button" class="btn-ghost icon-help" (click)="toggleHint('altura')" aria-label="Ajuda altura">?</button>
        </span>
                <input
                        class="input"
                        type="number"
                        min="1"
                        max="150"
                        step="1"
                        [placeholder]="DEFAULT_PKG.height.toString()"
                        [formControl]="transporteForm.controls.alturaCm" />
                <div class="hint-row">
                    <span class="small error" *ngIf="transporteForm.controls.alturaCm.errors?.['max']">Máximo 150 cm.</span>
                    <span class="small error" *ngIf="transporteForm.controls.alturaCm.errors?.['min']">Mínimo 1 cm.</span>
                    <span class="small muted" *ngIf="!transporteForm.controls.alturaCm.errors">Padrão: {{ DEFAULT_PKG.height }} cm</span>
                </div>
                <div class="note" *ngIf="hint.altura">
                    Limite: 150 cm. A soma (Comprimento + Largura + Altura) não pode exceder 200 cm.
                </div>
            </label>

            <!-- Largura -->
            <label>
        <span class="small">
          Largura (cm)
          <button type="button" class="btn-ghost icon-help" (click)="toggleHint('largura')" aria-label="Ajuda largura">?</button>
        </span>
                <input
                        class="input"
                        type="number"
                        min="1"
                        max="150"
                        step="1"
                        [placeholder]="DEFAULT_PKG.width.toString()"
                        [formControl]="transporteForm.controls.larguraCm" />
                <div class="hint-row">
                    <span class="small error" *ngIf="transporteForm.controls.larguraCm.errors?.['max']">Máximo 150 cm.</span>
                    <span class="small error" *ngIf="transporteForm.controls.larguraCm.errors?.['min']">Mínimo 1 cm.</span>
                    <span class="small muted" *ngIf="!transporteForm.controls.larguraCm.errors">Padrão: {{ DEFAULT_PKG.width }} cm</span>
                </div>
                <div class="note" *ngIf="hint.largura">
                    Limite: 150 cm. A soma (Comprimento + Largura + Altura) não pode exceder 200 cm.
                </div>
            </label>
        </div>

        <!-- Comprimento em linha separada + soma -->
        <div class="grid one">
            <label>
        <span class="small">
          Comprimento (cm)
          <button type="button" class="btn-ghost icon-help" (click)="toggleHint('comprimento')" aria-label="Ajuda comprimento">?</button>
        </span>
                <input
                        class="input"
                        type="number"
                        min="1"
                        max="150"
                        step="1"
                        [placeholder]="DEFAULT_PKG.length.toString()"
                        [formControl]="transporteForm.controls.comprimentoCm" />
                <div class="hint-row">
                    <span class="small error" *ngIf="transporteForm.controls.comprimentoCm.errors?.['max']">Máximo 150 cm.</span>
                    <span class="small error" *ngIf="transporteForm.controls.comprimentoCm.errors?.['min']">Mínimo 1 cm.</span>
                    <span class="small muted" *ngIf="!transporteForm.controls.comprimentoCm.errors">Padrão: {{ DEFAULT_PKG.length }} cm</span>
                </div>
                <div class="note" *ngIf="hint.comprimento">
                    Limite: 150 cm. A soma (Comprimento + Largura + Altura) não pode exceder 200 cm.
                </div>
            </label>

            <!-- Erro de soma -->
            <div class="hint-row" *ngIf="dimsSumExceeded">
                <span class="small error">A soma (C + L + A) não pode exceder 200 cm.</span>
            </div>
        </div>

        <div class="actions">
            <!-- Mensagens à esquerda -->
            <span class="small error" *ngIf="pesoAcimaLimite" style="margin-right:auto;">
        Peso informado ({{ transporteForm.value.pesoKg || 0 }} kg) excede {{ MAX_PESO }} kg.
      </span>
            <span class="small error" *ngIf="apiError && !pesoAcimaLimite" style="margin-right:auto;">
        {{ apiError }}
      </span>

            <!-- Botões -->
            <button class="btn btn-ghost" type="button" (click)="limparFreteForm()">Limpar</button>
            <button class="btn btn-ghost" type="button" (click)="closeModal()">Cancelar</button>
            <button
                    class="btn"
                    type="button"
                    [disabled]="loading || transporteForm.invalid || pesoAcimaLimite || dimsSumExceeded"
                    (click)="calcularFrete()">
                {{ loading ? 'Calculando…' : 'Calcular frete' }}
            </button>
            <button
                    class="btn btn-primary"
                    type="button"
                    [disabled]="!frete || pesoAcimaLimite || dimsSumExceeded"
                    (click)="aplicarFrete()">
                Aplicar ao orçamento
            </button>
        </div>

        <!-- RESULTADO -->
        <div class="quote" *ngIf="frete">
            <div class="small">Resultado</div>
            <div class="pill">Valor: {{ frete.valor | currency:'BRL':'symbol':'1.2-2' }}</div>
            <div class="pill" *ngIf="frete?.prazoDias != null">Prazo: {{ frete?.prazoDias }} dia(s)</div>

            <div class="small muted" *ngIf="frete.servicos?.length" style="width:100%; margin-top:8px;">Detalhe por serviço:</div>
            <table class="servicos" *ngIf="frete.servicos?.length">
                <thead>
                <tr><th>Serviço</th><th>Prazo</th><th>Valor</th></tr>
                </thead>
                <tbody>
                <tr *ngFor="let s of frete.servicos">
                    <td>{{ s.nome || s.codigo }}</td>
                    <td>{{ s.prazoDias ?? '-' }} d</td>
                    <td>{{ s.valor | currency:'BRL':'symbol':'1.2-2' }}</td>
                </tr>
                </tbody>
            </table>

            <p class="note">
                Valores <b>estimados</b> por {{ freteFonte || 'parceiro de frete' }} a partir dos dados informados
                (CEPs, peso e dimensões). São cotações indicativas e podem variar no ato da contratação/postagem.
                Se preferir, <b>informe um valor manual</b> no campo de transporte fora desta janela — esse valor
                terá prioridade sobre a cotação.
            </p>
        </div>
    </div>
</div>
