import { Component, OnInit } from '@angular/core';
import { RouterLink, RouterOutlet } from '@angular/router';

type Theme = 'light' | 'dark';

@Component({
  selector: 'app-root',
  standalone: true,
  imports: [RouterOutlet, RouterLink],
  template: `
  <header class="header">
    <a routerLink="/" class="brand" aria-label="Ir para in√≠cio">
      <span class="brand-badge">üõ†Ô∏è</span>
      <span>Or√ßamento de Pe√ßas</span>
    </a>

    <nav class="nav-right">
      <a routerLink="/meus" class="nav-link">Meus Or√ßamentos</a>
      <a routerLink="/novo" class="btn btn-primary">Novo Or√ßamento</a>
      <button class="btn btn-ghost" (click)="toggleTheme()">
        {{ theme === 'dark' ? 'Tema claro' : 'Tema escuro' }}
      </button>
    </nav>
  </header>

  <main class="container grid">
    <router-outlet></router-outlet>
  </main>
  `
})
export class AppComponent implements OnInit {
  theme: Theme = 'dark';

  ngOnInit() {
    const saved = localStorage.getItem('theme') as Theme | null;
    if (saved) {
      this.applyTheme(saved);
    } else {
      const prefersDark = window.matchMedia && window.matchMedia('(prefers-color-scheme: dark)').matches;
      this.applyTheme(prefersDark ? 'dark' : 'light');
    }
  }

  toggleTheme() { this.applyTheme(this.theme === 'dark' ? 'light' : 'dark'); }

  private applyTheme(t: Theme) {
    this.theme = t;
    document.documentElement.setAttribute('data-theme', t);
    localStorage.setItem('theme', t);
  }
}
