import { Component } from '@angular/core';
import { CommonModule } from '@angular/common';
import { FormsModule } from '@angular/forms';
import { Router, RouterLink } from '@angular/router';
import { AuthService } from '../../services/auth.service';

@Component({
    standalone: true,
    selector: 'app-login',
    imports: [CommonModule, FormsModule, RouterLink],
    templateUrl: './login.component.html',
    styleUrls: ['./login.component.css']
})
export class LoginComponent {
    email = '';
    password = '';
    error = '';

    constructor(private auth: AuthService, private router: Router) {}

    onSubmit() {
        this.error = '';
        const email = this.email.trim().toLowerCase();
        if (!email || !/^\S+@\S+\.\S+$/.test(email)) {
            this.error = 'Informe um e-mail válido.';
            return;
        }
        // mock: qualquer senha
        this.auth.login(email, this.password);
        this.router.navigate(['/meus']);
    }
}
<div class="grid form-shell">
    <div class="card grid" style="gap:16px;">
        <h2 style="margin:0;">Entrar</h2>
        <p class="small">Faça login para salvar e consultar seus orçamentos.</p>

        <div class="form-shell">
            <div>
                <label>E-mail</label>
                <input class="input" type="email" [(ngModel)]="email" placeholder="voce@exemplo.com" />
            </div>
            <div>
                <label>Senha</label>
                <input class="input" type="password" [(ngModel)]="password" placeholder="Sua senha" />
            </div>
            <div *ngIf="error" class="small" style="color:#ef4444;">{{error}}</div>
            <div style="display:flex; gap:8px; align-items:center;">
                <button class="btn btn-primary" (click)="onSubmit()">Entrar</button>
                <a routerLink="/novo" class="btn btn-ghost">Voltar</a>
            </div>
        </div>
    </div>
</div>
