import { Component, HostListener, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common';
import { ActivatedRoute, RouterLink } from '@angular/router';
import { OrcamentosService } from 'src/app/services/orcamentos.service';
import { ToastService } from 'src/app/services/toast.service';
import { DomSanitizer, SafeUrl } from '@angular/platform-browser';

type DetalheView = any & {
  fotos?: string[];
  fontesNovo?: string[];
  fontesUsado?: string[];
  createdAt?: string | null;
  criadoEm?: string | null;
  anosUso?: number;
  condicao?: string;
  urgencia?: string;
  temCaixaManual?: boolean;
  temNotaFiscal?: boolean;
  observacoes?: string | null;
  categoria?: string;
  modelo?: string;
  precoMin?: number;
  precoMax?: number;
  precoEscolhido?: number;
  mediaNovo?: number;
  mediaUsado?: number;
  slug?: string;
};

@Component({
  standalone: true,
  selector: 'app-result',
  imports: [CommonModule, RouterLink],
  templateUrl: './result.component.html',
  styleUrls: ['./result.component.css']
})
export class ResultComponent implements OnInit {
  slug = '';
  link = '';
  data?: DetalheView;
  loading = true;

  // público vs interno
  isPublic = false;

  // Lightbox
  viewerOpen = false;
  viewerIndex = 0;
  closing = false;

  constructor(
      private route: ActivatedRoute,
      private orcamentos: OrcamentosService,
      private toast: ToastService,
      private sanitizer: DomSanitizer
  ) {
    const slugParam = this.route.snapshot.paramMap.get('slug') || '';
    this.link = `${window.location.origin}/o/${slugParam}`;
    // define modo público pela rota
    this.isPublic = this.route.snapshot.data['public'] === true
        || this.route.snapshot.routeConfig?.path?.startsWith('o/')
        || this.route.snapshot.routeConfig?.path?.startsWith('orcamento/')
        || false;
  }

  ngOnInit(): void {
    this.slug = this.route.snapshot.paramMap.get('slug') || '';
    if (!this.slug) { this.loading = false; return; }

    this.orcamentos.getBySlug(this.slug).subscribe({
      next: (q) => {
        this.data = {
          ...q,
          fotos: q?.fotos ?? [],
          fontesNovo: (q as any)?.fontesNovo ?? [],
          fontesUsado: (q as any)?.fontesUsado ?? [],
          createdAt: (q as any)?.createdAt ?? (q as any)?.criadoEm ?? null,
        };
        this.loading = false;
      },
      error: () => { this.loading = false; }
    });
  }

  /* ===== Helpers de rótulo/estilo ===== */
  labelCondicao(c?: string) {
    switch ((c || '').toLowerCase()) {
      case 'impecavel': return 'Impecável';
      case 'bom': return 'Bom';
      default: return 'Regular';
    }
  }
  condClass(c?: string) {
    const v = (c || '').toLowerCase();
    return { ok: v === 'impecavel', warn: v === 'bom', bad: v === 'regular' };
  }
  labelUrgencia(u?: string) {
    switch ((u || '').toLowerCase()) {
      case 'imediata': return 'Imediata';
      case 'rapida': return 'Rápida';
      default: return 'Normal';
    }
  }
  urgClass(u?: string) {
    const v = (u || '').toLowerCase();
    return { bad: v === 'imediata', warn: v === 'rapida', ok: v === 'normal' };
  }

  /* ===== Fontes ===== */
  isUrl(s?: string): boolean {
    if (!s) return false;
    try { new URL(s); return true; } catch { return false; }
  }
  host(s: string): string {
    try { return new URL(s).hostname.replace(/^www\./, ''); } catch { return s; }
  }

  /* ===== Link público (somente interno) ===== */
  copy(): void {
    navigator.clipboard.writeText(this.link).then(
        () => this.toast.success('Link copiado!'),
        () => this.toast.warn('Não foi possível copiar o link.')
    );
  }

  /* ===== Fotos / Lightbox ===== */
  safeFoto(f: string): SafeUrl {
    return this.sanitizer.bypassSecurityTrustUrl(f);
  }
  get currentPhoto(): string {
    const list = this.data?.fotos ?? [];
    const cap = Math.min(list.length, 5);
    return cap ? list[this.viewerIndex % cap] : '';
  }
  openPhoto(i: number) {
    this.viewerIndex = i;
    this.closing = false;
    this.viewerOpen = true;
  }
  closeViewer() {
    if (!this.viewerOpen) return;
    this.closing = true;
    setTimeout(() => {
      this.viewerOpen = false;
      this.closing = false;
    }, 160);
  }
  next() {
    const cap = Math.min(this.data?.fotos?.length ?? 0, 5);
    if (cap) this.viewerIndex = (this.viewerIndex + 1) % cap;
  }
  prev() {
    const cap = Math.min(this.data?.fotos?.length ?? 0, 5);
    if (cap) this.viewerIndex = (this.viewerIndex - 1 + cap) % cap;
  }

  @HostListener('document:keydown.escape') onEsc() { if (this.viewerOpen) this.closeViewer(); }
  @HostListener('document:keydown.arrowRight') onRight() { if (this.viewerOpen) this.next(); }
  @HostListener('document:keydown.arrowLeft') onLeft() { if (this.viewerOpen) this.prev(); }

  trackByIndex(i: number) { return i; }
}
<ng-container *ngIf="data as o; else notFound">
    <div class="grid form-shell">
        <div class="card grid" style="gap:16px;">

            <!-- Cabeçalho -->
            <div class="header">
                <div>
                    <div class="small">
                        Orçamento • {{ (o.createdAt || o.criadoEm) | date:'short' }}
                    </div>
                    <h2 class="title">{{ o.categoria }} — {{ o.modelo }}</h2>
                </div>
                <!-- botão só na rota interna -->
                <a *ngIf="!isPublic" routerLink="/novo" class="btn btn-ghost">Novo orçamento</a>
            </div>

            <!-- Meta row (não clicável) -->
            <ul class="meta-row">
                <li class="meta-item">
                    <span class="meta-label">Caixa manual</span>
                    <span class="meta-value" [class.ok]="o.temCaixaManual">{{ o.temCaixaManual ? 'Sim' : 'Não' }}</span>
                </li>
                <li class="meta-item">
                    <span class="meta-label">Nota fiscal</span>
                    <span class="meta-value" [class.ok]="o.temNotaFiscal">{{ o.temNotaFiscal ? 'Sim' : 'Não' }}</span>
                </li>
                <li class="meta-item">
                    <span class="meta-label">Anos de uso</span>
                    <span class="meta-value">{{ o.anosUso }}</span>
                </li>
                <li class="meta-item">
                    <span class="meta-label">Condição</span>
                    <span class="meta-value" [ngClass]="condClass(o.condicao)">{{ labelCondicao(o.condicao) }}</span>
                </li>
                <li class="meta-item">
                    <span class="meta-label">Urgência</span>
                    <span class="meta-value" [ngClass]="urgClass(o.urgencia)">{{ labelUrgencia(o.urgencia) }}</span>
                </li>
            </ul>

            <!-- Faixa e escolhido -->
            <div class="cards-grid">
                <div class="card small-card">
                    <div class="small">Faixa sugerida</div>
                    <div class="price">
                        {{ o.precoMin | currency:'BRL':'symbol':'1.0-0' }} – {{ o.precoMax | currency:'BRL':'symbol':'1.0-0' }}
                    </div>
                </div>
                <div class="card small-card">
                    <div class="small">Valor escolhido</div>
                    <div class="price">{{ o.precoEscolhido | currency:'BRL':'symbol':'1.0-0' }}</div>
                </div>
            </div>

            <!-- Médias + Fontes (preparado) -->
            <div class="cards-grid">
                <div class="card small-card">
                    <div class="small">Média do novo</div>
                    <div class="price">{{ o.mediaNovo | currency:'BRL':'symbol':'1.0-0' }}</div>
                    <div class="sources">
                        <div class="small muted">Fontes ({{ o.fontesNovo?.length || 0 }})</div>
                        <ul class="source-list" *ngIf="o.fontesNovo?.length; else noSourcesNovo">
                            <li *ngFor="let s of o.fontesNovo; trackBy: trackByIndex">
                                <a *ngIf="isUrl(s)" [href]="s" target="_blank" rel="noopener">{{ host(s) }}</a>
                                <span *ngIf="!isUrl(s)">{{ s }}</span>
                            </li>
                        </ul>
                        <ng-template #noSourcesNovo>
                            <div class="small softer">Aguardando ingestão…</div>
                        </ng-template>
                    </div>
                </div>

                <div class="card small-card">
                    <div class="small">Média do usado</div>
                    <div class="price">{{ o.mediaUsado | currency:'BRL':'symbol':'1.0-0' }}</div>
                    <div class="sources">
                        <div class="small muted">Fontes ({{ o.fontesUsado?.length || 0 }})</div>
                        <ul class="source-list" *ngIf="o.fontesUsado?.length; else noSourcesUsado">
                            <li *ngFor="let s of o.fontesUsado; trackBy: trackByIndex">
                                <a *ngIf="isUrl(s)" [href]="s" target="_blank" rel="noopener">{{ host(s) }}</a>
                                <span *ngIf="!isUrl(s)">{{ s }}</span>
                            </li>
                        </ul>
                        <ng-template #noSourcesUsado>
                            <div class="small softer">Aguardando ingestão…</div>
                        </ng-template>
                    </div>
                </div>
            </div>

            <!-- Observações -->
            <div class="card" *ngIf="o.observacoes">
                <div class="small">Observações</div>
                <p class="obs">{{ o.observacoes }}</p>
            </div>

            <!-- Fotos -->
            <div>
                <div class="small">Fotos ({{ (o.fotos?.length || 0) > 5 ? 5 : (o.fotos?.length || 0) }}/5)</div>
                <div class="photos thumb-grid" *ngIf="o.fotos?.length; else noPhotos">
                    <button class="thumb-wrap"
                            *ngFor="let f of o.fotos | slice:0:5; let i = index; trackBy: trackByIndex"
                            type="button" (click)="openPhoto(i)"
                            [title]="'Abrir foto ' + (i + 1)">
                        <img class="thumb" [src]="safeFoto(f)" [alt]="'Foto ' + (i + 1)" />
                        <span class="badge">{{ i + 1 }}</span>
                    </button>
                </div>
                <ng-template #noPhotos><div class="small">Sem fotos</div></ng-template>
            </div>

            <!-- Lightbox -->
            <div class="lightbox" *ngIf="viewerOpen" [class.closing]="closing"
                 (click)="closeViewer()" tabindex="-1" aria-modal="true" role="dialog">
                <div class="lightbox-inner" (click)="$event.stopPropagation()">
                    <img [src]="safeFoto(currentPhoto)" [alt]="'Foto ' + (viewerIndex + 1)" />
                    <button class="close" type="button" (click)="closeViewer()" aria-label="Fechar">×</button>
                </div>
            </div>

            <!-- Link público — só na rota interna -->
            <div class="card" *ngIf="!isPublic">
                <div class="small">Link público</div>
                <div style="display:flex; gap:8px; align-items:center; flex-wrap:wrap;">
                    <code style="background:color-mix(in oklab, var(--bg-2) 60%, transparent); border:1px solid var(--border); padding:8px 10px; border-radius:8px;">
                        {{ link }}
                    </code>
                    <button class="btn btn-ghost" (click)="copy()">Copiar</button>
                    <a class="btn btn-primary" [href]="'/orcamento/' + (o?.slug || '')">Abrir</a>
                </div>
            </div>

        </div>
    </div>
</ng-container>

<ng-template #notFound>
    <div class="card form-shell">Orçamento não encontrado.</div>
</ng-template>
