import { Component, OnInit } from '@angular/core';
import { RouterLink, RouterOutlet } from '@angular/router';
import { AuthService } from './services/auth.service';

type Theme = 'light' | 'dark';

@Component({
  selector: 'app-root',
  standalone: true,
  imports: [RouterOutlet, RouterLink],
  template: `
    <header class="header">
      <a routerLink="/" class="brand" aria-label="Ir para in√≠cio">
        <span class="brand-badge">üõ†Ô∏è</span>
        <span>Or√ßamento por Partes</span>
      </a>

      <nav class="nav-right">
        <!-- aparece s√≥ se logado -->
        <a *ngIf="logged" routerLink="/meus" class="nav-link">Meus Or√ßamentos</a>

        <!-- aparece s√≥ se N√ÉO logado -->
        <a *ngIf="!logged" routerLink="/login" class="nav-link">Login</a>

        <!-- bot√£o sair s√≥ se logado -->
        <button *ngIf="logged" class="nav-link" (click)="logout()">Sair</button>

        <!-- sempre vis√≠vel -->
        <a routerLink="/novo" class="btn btn-primary">Novo Or√ßamento</a>

        <!-- tema -->
        <button class="btn btn-ghost" (click)="toggleTheme()">
          {{ theme === 'dark' ? 'Tema claro' : 'Tema escuro' }}
        </button>
      </nav>
    </header>

    <main class="container grid">
      <router-outlet></router-outlet>
    </main>
  `
})
export class AppComponent implements OnInit {
  theme: Theme = 'dark';

  constructor(private auth: AuthService) {}

  /** Getter evita qualquer problema de detec√ß√£o no template */
  get logged(): boolean {
    return this.auth.isLoggedIn();
  }

  ngOnInit() {
    const saved = localStorage.getItem('theme') as Theme | null;
    if (saved) {
      this.applyTheme(saved);
    } else {
      const prefersDark = window.matchMedia && window.matchMedia('(prefers-color-scheme: dark)').matches;
      this.applyTheme(prefersDark ? 'dark' : 'light');
    }
  }

  toggleTheme() {
    this.applyTheme(this.theme === 'dark' ? 'light' : 'dark');
  }

  logout() {
    this.auth.logout();
    // for√ßa atualiza√ß√£o visual ap√≥s logout
    // (como usamos getter, normalmente j√° atualiza; isso s√≥ garante)
    setTimeout(() => {}, 0);
  }

  private applyTheme(t: Theme) {
    this.theme = t;
    document.documentElement.setAttribute('data-theme', t);
    localStorage.setItem('theme', t);
  }
}
