t-20[_ngcontent-%COMP%] { margin-top: 20px; }\n.price-line[_ngcontent-%COMP%] { display:flex; align-items:center; justify-content:space-between; margin: 8px 0 6px; }\n.muted[_ngcontent-%COMP%] { opacity: .8; }\n\n.manual-price[_ngcontent-%COMP%] { margin-top: 10px; }\n.input-row[_ngcontent-%COMP%] { display:flex; align-items:center; gap: 8px; }\n.prefix[_ngcontent-%COMP%] {\n    padding: 8px 10px; border-radius: 10px;\n    border: 1px solid var(--border);\n    background: color-mix(in oklab, var(--bg-2) 75%, transparent);\n}\n\n.note.warning[_ngcontent-%COMP%] {\n    margin-top: 6px;\n    font-size: 14px;\n    color: #b91c1c;            \n\n}\n[_ngcontent-%COMP%]:root[data-theme=\"dark\"]   .note.warning[_ngcontent-%COMP%] {\n    color: #fca5a5;            \n\n}\n\n.currency-input[_ngcontent-%COMP%] { position: relative; }\n\n\n\n.currency-input[_ngcontent-%COMP%]   .input.currency[_ngcontent-%COMP%] {\n    padding-left: 16px;          \n\n    padding-right: 56px;         \n\n}\n\n\n\n.currency-input[_ngcontent-%COMP%]   .steppers[_ngcontent-%COMP%] {\n    position: absolute;\n    right: 8px; top: 50%; transform: translateY(-50%);\n    display: flex; flex-direction: column; gap: 6px;\n}\n\n.currency-input[_ngcontent-%COMP%]   .stepper[_ngcontent-%COMP%] {\n    width: 26px; height: 20px;\n    display: inline-flex; align-items: center; justify-content: center;\n    border-radius: 8px;\n    border: 1px solid var(--accent);\n    background: color-mix(in oklab, var(--accent) 16%, transparent);\n    color: var(--accent);\n    font-size: 12px; line-height: 1;\n    cursor: pointer;\n    transition: transform .06s ease, background .2s ease, color .2s ease, box-shadow .2s ease;\n}\n.currency-input[_ngcontent-%COMP%]   .stepper[_ngcontent-%COMP%]:hover {\n    background: color-mix(in oklab, var(--accent) 34%, transparent);\n    color: #fff; box-shadow: 0 0 0 4px var(--ring);\n}\n.currency-input[_ngcontent-%COMP%]   .stepper[_ngcontent-%COMP%]:active { transform: translateY(1px); }\n\n\n\n.input.number[_ngcontent-%COMP%]::-webkit-outer-spin-button, .input.number[_ngcontent-%COMP%]::-webkit-inner-spin-button { -webkit-appearance: none; margin: 0; }\n.input.number[_ngcontent-%COMP%] { -moz-appearance: textfield; }\n\n/*# sourceMappingURL=data:application/json;charset=utf-8;base64, */"],
      data: {
        animation: [trigger('fadeSlideIn', [transition(':enter', [style({
          opacity: 0,
          transform: 'translateY(10px)'
        }), animate('180ms ease-out', style({
          opacity: 1,
          transform: 'none'
        }))])])]
      }
    });
  }
}

