import { Component, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common';
import { FormsModule } from '@angular/forms';
import { Router } from '@angular/router';
import { PhotoUploaderComponent } from '../../components/photo-uploader/photo-uploader.component';
import { QuoteInput } from '../../models';
import { trigger, transition, style, animate } from '@angular/animations';
import { AuthService } from 'src/app/services/auth.service';
import { OrcamentoPayload  } from 'src/app/models/orcamento.model';
import { OrcamentosService } from 'src/app/services/orcamentos.service';
import { NgxMaskDirective } from 'ngx-mask';

@Component({
  standalone: true,
  selector: 'app-wizard',
  imports: [CommonModule, FormsModule, PhotoUploaderComponent, NgxMaskDirective],
  templateUrl: './wizard.component.html',
  styleUrls: ['./wizard.component.css'],
  animations: [
    trigger('fadeSlideIn', [
      transition(':enter', [
        style({ opacity: 0, transform: 'translateY(10px)' }),
        animate('180ms ease-out', style({ opacity: 1, transform: 'none' }))
      ])
    ])
  ]
})

export class WizardComponent implements OnInit {
  get sliderMax(): number {
    const sug = this.model.priceMax ?? this.MAX_PRICE;
    return Math.min(sug, this.MAX_PRICE);
  }

  get chosenPercent(): number {
    const min = this.model.priceMin ?? 0;
    const max = this.sliderMax;        // <-- aqui
    const val = this.model.chosenPrice ?? min;
    if (max <= min) return 0;
    return Math.max(0, Math.min(100, Math.round(((val - min) * 100) / (max - min))));
  }

  get canNext(): boolean {
    switch (this.step) {
      case 1: return true;
      case 2: return !!(this.model.category && this.model.model);
      case 3: return this.model.yearsUsed >= 0 && !!this.model.condition;
      case 4: return true;
      case 5: return true;
      case 6: return this.comparisonDone; // ⬅️ só libera a seta após comparação concluída
      case 7: return this.model.chosenPrice != null;
      case 8: return true;
      case 9: return true;
      case 10: return true;
      default: return false;
    }
  }

  get canPrev(): boolean { return this.step > 1; }

  get isAboveMax(): boolean {
    const max = this.model.priceMax ?? null;
    const val = this.model.chosenPrice ?? null;
    return max != null && val != null && val > max;
  }

  model: QuoteInput = {
    category: '',
    model: '',
    yearsUsed: 0,
    condition: 'bom',
    urgency: 'normal',
    hasBox: false,
    hasInvoice: false,
    notes: '',
    photos: [],
    newPriceAvg: undefined,
    newPriceSources: [],
    usedPriceAvg: undefined,
    usedPriceSources: [],
    priceMin: undefined,
    priceMax: undefined,
    chosenPrice: undefined
  };

  readonly MAX_PRICE = 1_000_000_000;
  comparisonDone = false;
  step = 1;
  loading = false;
  generating = false;
  loadingMessage = '';
  loadingQueue = [
    'Comparando preços com peças novas...',
    'Comparando preços com anúncios existentes...',
    'Verificando anúncios de usados...'
  ];

  constructor(private router: Router,  private quotes: OrcamentosService, private auth: AuthService) {}

  ngOnInit() {
    this.model = {
      category: '', model: '', yearsUsed: 0, condition: 'bom',
      urgency: 'normal', hasBox: false, hasInvoice: false,
      notes: '', photos: [],
      newPriceAvg: 0, usedPriceAvg: 0, priceMin: 0, priceMax: 0, chosenPrice: 0
    };
    this.step = 1;
  }

  next() {
    if (!this.canNext) return;
    if (this.step === 6 && !this.comparisonDone) return;
    if (this.step < 10) this.step++;
  }

  prev() { if (this.canPrev) this.step--; }

  onStartN8n() {
    if (this.loading) return;
    this.loading = true;
    this.comparisonDone = false;      // ⬅️ reset
    this.loadingMessage = this.loadingQueue[0];

    let idx = 0;
    const tick = () => {
      if (idx < this.loadingQueue.length - 1) {
        idx++;
        this.loadingMessage = this.loadingQueue[idx];
        setTimeout(tick, 1200);
      } else {
        this.model.newPriceAvg = Math.round(1200 + Math.random()*200);
        this.model.newPriceSources = ['Loja A', 'Loja B', 'Loja C'];
        this.model.usedPriceAvg = Math.round(700 + Math.random()*150);
        this.model.usedPriceSources = ['Marketplace X', 'Marketplace Y', 'Grupo Z'];
        const min = Math.round((this.model.usedPriceAvg || 0) * 0.85);
        const max = Math.round((this.model.usedPriceAvg || 0) * 1.20);
        this.model.priceMin = min;
        this.model.priceMax = max;
        this.model.chosenPrice = Math.round((min + max) / 2);

        this.loading = false;
        this.comparisonDone = true;
      }
    };
    setTimeout(tick, 1200);
  }

  onGenerate() {
    const body: OrcamentoPayload = {
      usuarioId: this.auth.getUserId(),
      categoria: this.model.category,
      modelo: this.model.model,
      anosUso: this.model.yearsUsed,
      condicao: this.model.condition as any,
      urgencia: this.model.urgency as any,
      temCaixaManual: this.model.hasBox,
      temNotaFiscal: this.model.hasInvoice,
      observacoes: this.model.notes,

      mediaNovo: this.model.newPriceAvg,
      mediaUsado: this.model.usedPriceAvg,
      precoMin: this.model.priceMin,
      precoMax: this.model.priceMax,
      precoEscolhido: this.model.chosenPrice,

      fotos: this.model.photos || []
    };

    this.generating = true;
    this.quotes.criar(body).subscribe({
      next: res => this.router.navigate(['/resultado', res.slug]),
      error: () => this.generating = false
    });
  }

  clampChosen(val: any) {
    const n = Math.max(0, Math.min(this.MAX_PRICE, Number(val) || 0));
    this.model.chosenPrice = Math.round(n);
  }

  stepPrice(delta: number) {
    this.clampChosen((this.model.chosenPrice || 0) + delta);
  }
}
<div class="grid form-shell">
    <div class="card grid" style="gap:20px;">
        <div style="display:flex; align-items:center; justify-content:space-between;">
            <h2>Faça seu orçamento</h2>
            <div class="step-indicator">Etapa {{step}} de 10</div>
        </div>

        <div class="progress">
            <div class="bar" [style.width.%]="(step/10)*100"></div>
        </div>

        <!-- VIEWPORT com altura mínima: nada de “pulo” visual -->
        <div class="step-viewport">
            <ng-container [ngSwitch]="step">

                <!-- 1 -->
                <section *ngSwitchCase="1" @fadeSlideIn>
                    <p class="small">Nas etapas seguintes, preencha os dados e detalhes sobre o produto. </p>
                    <p class="small">Cada etapa habilita a próxima quando os campos obrigatórios estiverem completos. </p>
                </section>

                <!-- 2 -->
                <section *ngSwitchCase="2" @fadeSlideIn>
                    <div class="form-shell">
                        <div>
                            <label>Categoria da peça</label>
                            <select [(ngModel)]="model.category">
                                <option value="" disabled selected>Selecione...</option>
                                <option value="CPU">CPU</option>
                                <option value="GPU">GPU</option>
                                <option value="RAM">RAM</option>
                                <option value="SSD">SSD</option>
                                <option value="MB">Placa-mãe</option>
                                <option value="PSU">Fonte</option>
                                <option value="OUTROS">Outros</option>
                            </select>
                        </div>
                        <div>
                            <label>Modelo</label>
                            <input class="input" [(ngModel)]="model.model" placeholder="Ex.: Ryzen 7 3700X" />
                        </div>
                    </div>
                </section>

                <!-- 3 -->
                <section *ngSwitchCase="3" @fadeSlideIn>
                    <div class="form-shell">
                        <div>
                            <label>Anos de uso</label>
                            <input class="input" type="number" [(ngModel)]="model.yearsUsed" min="0" max="25"/>
                        </div>
                        <div>
                            <label>Condição</label>
                            <select [(ngModel)]="model.condition">
                                <option value="impecavel">Impecável</option>
                                <option value="bom">Bom</option>
                                <option value="regular">Regular</option>
                            </select>
                        </div>
                    </div>
                </section>

                <!-- 4 -->
                <section *ngSwitchCase="4" @fadeSlideIn>
                    <div class="form-shell">
                        <div>
                            <label>Urgência de venda</label>
                            <select [(ngModel)]="model.urgency">
                                <option value="normal">Normal</option>
                                <option value="rapida">Rápida (até alguns dias)</option>
                                <option value="imediata">Imediata (48h)</option>
                            </select>
                        </div>
                        <div style="display:flex; gap:16px; align-items:center; margin-top:auto;">
                            <label><input type="checkbox" [(ngModel)]="model.hasBox" /> Tem caixa/manual</label>
                            <label><input type="checkbox" [(ngModel)]="model.hasInvoice" /> Tem nota fiscal</label>
                        </div>
                    </div>
                </section>

                <!-- 5 -->
                <section *ngSwitchCase="5" @fadeSlideIn>
                    <div class="form-shell">
                        <div>
                            <label>Observações</label>
                            <textarea
                                    class="no-resize"
                                    rows="5"
                                    maxlength="300"
                                    [(ngModel)]="model.notes"
                                    placeholder="Ex.: primeiro dono, sem overclock..."></textarea>
                            <div class="small right">{{ (model.notes?.length || 0) }}/300</div>
                        </div>
                    </div>
                </section>

                <!-- 6 -->
                <section *ngSwitchCase="6" @fadeSlideIn>
                    <div class="form-shell">
                        <div class="card">

                            <!-- Estado inicial -->
                            <div class="small" *ngIf="!loading && !comparisonDone">
                                Pronto para comparar preços.
                            </div>

                            <!-- Loading local da comparação -->
                            <div *ngIf="loading">
                                <p class="small">{{loadingMessage}}</p>
                                <div aria-busy="true" class="small">Carregando...</div>
                            </div>

                            <!-- Concluído: apenas informa que pode avançar -->
                            <div *ngIf="!loading && comparisonDone">
                                <p class="small">✅ Comparação finalizada. Avance para conferir os valores.</p>
                            </div>

                            <div style="display:flex; gap:12px; margin-top:12px;">
                                <!-- Botão aparece só antes de começar -->
                                <button
                                        class="btn btn-primary"
                                        (click)="onStartN8n()"
                                        *ngIf="!loading && !comparisonDone">
                                    Iniciar comparação
                                </button>
                            </div>
                        </div>
                    </div>
                </section>

                <!-- 7 -->
                <section *ngSwitchCase="7" @fadeSlideIn>
                    <div class="form-shell">
                        <div class="card">
                            <div class="small">Média do valor da peça nova</div>
                            <div class="price">R$ {{model.newPriceAvg || 0 | number:'1.0-0'}}</div>
                            <div class="small">Fontes:</div>
                            <ul class="small"><li *ngFor="let s of model.newPriceSources">{{s}}</li></ul>
                        </div>

                        <div class="card">
                            <div class="small">Média do valor da peça usada</div>
                            <div class="price">R$ {{model.usedPriceAvg || 0 | number:'1.0-0'}}</div>
                            <div class="small">Fontes:</div>
                            <ul class="small"><li *ngFor="let s of model.usedPriceSources">{{s}}</li></ul>
                        </div>
                    </div>

                    <!-- 👇 mais respiro entre o 2º card e o seletor -->
                    <div class="card mt-20">
                        <div class="small">Valor <b>sugerido</b> (ajustável)</div>

                        <!-- range maior e knob/“arrastar” verde -->
                        <input
                                type="range"
                                class="range"
                                [min]="model.priceMin || 0"
                                [max]="model.priceMax || 0"
                                [step]="1"
                                [(ngModel)]="model.chosenPrice"
                                (ngModelChange)="model.chosenPrice = +$event"
                                [style.--p]="chosenPercent + '%'"
                                aria-label="Valor sugerido"
                        />

                        <p class="small muted">
                            Este valor é <b>sugerido</b>, não obrigatório. Você pode ajustar pela barra
                            ou digitar manualmente abaixo.
                        </p>

                        <div class="price-line">
                            <div>Escolhido: <b>R$ {{model.chosenPrice || 0 | number:'1.0-0'}}</b></div>
                            <div class="small">Min: {{model.priceMin}} • Max: {{model.priceMax}}</div>
                        </div>

                        <p *ngIf="isAboveMax" class="note warning">*Você ultrapassou o limite sugerido. (Isso não bloqueia o orçamento).</p>

                        <!-- entrada manual -->
                        <div class="manual-price">
                            <label class="small">OU INFORME MANUALMENTE</label>

                            <div class="currency-input">
                                <input
                                        class="input number currency"
                                        type="text"
                                        inputmode="numeric"
                                        [mask]="'separator.0'"
                                        [thousandSeparator]="'.'"
                                        [prefix]="'R$ '"
                                        [dropSpecialCharacters]="true"
                                        [allowNegativeNumbers]="false"
                                        [(ngModel)]="model.chosenPrice"
                                        (ngModelChange)="clampChosen($event)"
                                        placeholder="R$ 0"
                                        aria-label="Preço manual"
                                />

                                <!-- setinhas custom sempre visíveis -->
                                <div class="steppers" role="group" aria-label="Ajustar valor">
                                    <button type="button" class="stepper up" (click)="stepPrice(+1)" aria-label="Aumentar">▲</button>
                                    <button type="button" class="stepper down" (click)="stepPrice(-1)" aria-label="Diminuir">▼</button>
                                </div>
                            </div>
                        </div>
                    </div>
                </section>

                <!-- 8 -->
                <section *ngSwitchCase="8" @fadeSlideIn>
                    <div class="form-shell">
                        <div>
                            <label>Fotos (até 5)</label>
                            <app-photo-uploader [(value)]="model.photos"></app-photo-uploader>
                        </div>
                    </div>
                </section>

                <!-- 9 -->
                <section *ngSwitchCase="9" @fadeSlideIn>
                    <div class="card grid" style="gap:10px;">
                        <div><b>Categoria:</b> {{model.category}}</div>
                        <div><b>Modelo:</b> {{model.model}}</div>
                        <div><b>Anos de uso:</b> {{model.yearsUsed}}</div>
                        <div><b>Condição:</b> {{model.condition}}</div>
                        <div><b>Urgência:</b> {{model.urgency}}</div>
                        <div><b>Caixa:</b> {{model.hasBox ? 'sim':'não'}} • <b>NF:</b> {{model.hasInvoice ? 'sim':'não'}}</div>
                        <div><b>Observações:</b> {{model.notes || '-'}}</div>
                        <div><b>Faixa sugerida:</b> R$ {{model.priceMin}} — R$ {{model.priceMax}}</div>
                        <div><b>Valor escolhido:</b> R$ {{model.chosenPrice}}</div>
                    </div>
                </section>

                <!-- 10 -->
                <section *ngSwitchCase="10" @fadeSlideIn>
                    <div class="card">
                        <button class="btn btn-primary"
                                (click)="onGenerate()"
                                [disabled]="generating">
                            {{ generating ? 'Gerando…' : 'Gerar orçamento' }}
                        </button>
                    </div>
                </section>

            </ng-container>
        </div>

        <!-- NAV -->
        <div class="wizard-nav">
            <button *ngIf="step > 1" class="btn-arrow" (click)="prev()" aria-label="Voltar">←</button>
            <span class="flex-spacer"></span>
            <button *ngIf="step < 10 && canNext" class="btn-arrow" (click)="next()" aria-label="Avançar">→</button>
        </div>
    </div>
</div>
