import { Routes } from '@angular/router';
import { WizardComponent } from './pages/wizard/wizard.component';
import { ResultComponent } from './pages/result/result.component';
import { PublicComponent } from './pages/public/public.component';
import { ListComponent } from './pages/list/list.component';
import { LoginComponent } from './pages/login/login.component';
import { authGuard } from './guards/auth.guard';   // ðŸ‘ˆ
import { guestGuard } from './guards/guest.guard'; // ðŸ‘ˆ

export const routes: Routes = [
  { path: '', redirectTo: 'novo', pathMatch: 'full' },
  { path: 'novo', component: WizardComponent },                       // livre (sem login)
  { path: 'meus', component: ListComponent, canActivate: [authGuard] }, // ðŸ‘ˆ protegido
  { path: 'login', component: LoginComponent, canActivate: [guestGuard] }, // ðŸ‘ˆ sÃ³ visitantes
  { path: 'resultado/:slug', component: ResultComponent },
  { path: 'o/:slug', component: PublicComponent },
  { path: '**', redirectTo: 'novo' }
];
